export const loginFormStyle = {
    display: "flex",
    margin: "auto",
    justifyContent: "space-evenly",
    alignItems: "center",
    backgroundColor: "#222222",
    boxShadow: "1px 1px 5px rgba(0, 0, 0, 0.5)",
    borderRadius: "16px",
    width: "400px",
    height: "300px",
    padding: "1rem",
};

export const textFieldStyle = {
    borderColor: "white",
    borderRadius: "8px",
    backgroundColor: "white",
    marginBottom: "2px",
    textAlign: "center",
};

export const submitButton = {
    height: "45px", 
    width: "207px",
